from telliot_feeds.queries.ampleforth.ampl_usd_vwap import AMPLUSDVWAP


def test_ampl_usd_vwap_query():
    """Validate AMPLUSDVWAP query id and data"""
    q = AMPLUSDVWAP()

    print(q.query_data.hex())
    print(q.query_id.hex())

    assert q.query_data.hex() == ""
    assert q.query_id.hex() == "f42cfaef1fd6bc6ce5e992b7175c0dcd5e971e8863b3e7e4e1b4031a73effc1c"